const newsletters = [
  { id: 'the-rundown-ai', name: 'The Rundown AI', signup_url: 'https://www.therundown.ai/', rss_url: null },
  { id: 'bens-bites', name: 'Ben\'s Bites', signup_url: 'https://www.bensbites.com/', rss_url: null },
  { id: 'superhuman-ai', name: 'Superhuman AI', signup_url: 'https://www.superhuman.ai/', rss_url: null },
  { id: 'import-ai', name: 'Import AI', signup_url: 'https://importai.substack.com/', rss_url: 'https://importai.substack.com/feed' },
  { id: 'ai-snake-oil', name: 'AI Snake Oil', signup_url: 'https://aisnakeoil.substack.com/', rss_url: 'https://aisnakeoil.substack.com/feed' },
  { id: 'gary-marcus', name: 'Marcus on AI', signup_url: 'https://garymarcus.substack.com/', rss_url: 'https://garymarcus.substack.com/feed' },
  { id: 'the-batch', name: 'The Batch', signup_url: 'https://www.deeplearning.ai/the-batch/', rss_url: null },
  { id: 'tldr-ai', name: 'TLDR AI', signup_url: 'https://tldr.tech/ai', rss_url: null },
  { id: 'alphasignal', name: 'AlphaSignal', signup_url: 'https://alphasignal.ai/', rss_url: null },
  { id: 'chain-of-thought', name: 'Chain of Thought (Every)', signup_url: 'https://every.to/chain-of-thought', rss_url: null }
];

return newsletters.map((item, index) => ({
  json: {
    rank: index + 1,
    ...item,
    action: 'Open signup_url and subscribe with recursifai@gmail.com',
    notes: item.rss_url ? 'RSS already usable; email signup optional but recommended' : 'No reliable public RSS; use Gmail subscription for ingestion fallback'
  }
}));

const input = $input.all();
return input.map((item) => {
  const statusCode = item.json.statusCode || null;
  const ok = typeof statusCode === 'number' ? (statusCode >= 200 && statusCode < 400) : false;
  return {
    json: {
      rank: item.json.rank,
      id: item.json.id,
      name: item.json.name,
      signup_url: item.json.signup_url,
      rss_url: item.json.rss_url,
      action: item.json.action,
      notes: item.json.notes,
      probe_status_code: statusCode,
      probe_ok: ok
    }
  };
});
